    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_swipe_main_page);

        tabLayout  = findViewById(R.id.tabLayout);

        tabLayout.getTabAt(0).setIcon(ICONS[0]);
        tabLayout.getTabAt(1).setIcon(ICONS[1]);
        tabLayout.getTabAt(2).setIcon(ICONS[2]);
        tabLayout.getTabAt(3).setIcon(ICONS[3]);
        tabLayout.getTabAt(4).setIcon(ICONS[4]);
        tabLayout.getTabAt(5).setIcon(ICONS[5]);


        tabLayout.addOnTabSelectedListener(new TabLayout.OnTabSelectedListener() {
            @Override
            public void onTabSelected(TabLayout.Tab tab) {
                switchCase(tab.getPosition());

            }

            @Override
            public void onTabUnselected(TabLayout.Tab tab) {

            }

            @Override
            public void onTabReselected(TabLayout.Tab tab) {
                switchCase(tab.getPosition());
            }
        });

        //call the covid background function
        covidBackground();

        //call the covid variant function
        covidVariant();

        //call the ways how covid spread function
        howCovidSpread();

        //call the ways to prevent covid function
        getPreventiveSteps();

        //call the covid symptom function
        covidSymptom();



        //declare button left
        ImageButton swipeLeft = (ImageButton) findViewById(R.id.btn_left);
        swipeLeft.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent swipeLeftIntent = new Intent(SwipeMain.this, SwipeLeft.class);
                startActivity(swipeLeftIntent);
            }
        });

        //declare button right
        ImageButton swipeRight = (ImageButton) findViewById(R.id.btn_right);
        swipeRight.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent swipeRightIntent = new Intent(SwipeMain.this, SwipeRight.class);
                startActivity(swipeRightIntent);
            }
        });


    }

    private void switchCase(int position){
        switch (position){
            case 0:
                intent = new Intent(SwipeMain.this,SwipeMain.class);
                startActivity(intent);
                break;

            case 1:
                intent = new Intent(SwipeMain.this,Scraper.class);
                startActivity(intent);
                break;

            case 2:
                intent = new Intent(SwipeMain.this,MapsFragment.class);
                startActivity(intent);
                break;

            case 3:
                intent = new Intent(SwipeMain.this, Fingerprint.class);
                startActivity(intent);
                break;

            case 4:
                intent = new Intent(SwipeMain.this, Profile.class);
                startActivity(intent);
                break;

            case 5:
                showAlertDialog();
                break;

        }
    }

    private void showAlertDialog(){

        AlertDialog.Builder alert = new AlertDialog.Builder(this);
            alert.setTitle("Logout");
            alert.setMessage("Are you sure you want to logout?");
            alert.setPositiveButton("Logout", new DialogInterface.OnClickListener() {
                @Override
                public void onClick(DialogInterface dialog, int which) {
                    Toast.makeText(SwipeMain.this,"Logged Out",Toast.LENGTH_SHORT).show();
                    //mAuth.signOut();
                    intent = new Intent(SwipeMain.this, MainActivity.class);
                    startActivity(intent);
                }
            });
            alert.setNegativeButton("No", new DialogInterface.OnClickListener() {
                @Override
                public void onClick(DialogInterface dialog, int which) {
                    intent = new Intent(SwipeMain.this, SwipeMain.class);
                    startActivity(intent);
                }
            });
            alert.create().show();

    }

