    private void getPreventiveSteps(){
        Log.d(TAG, "initImageBitmaps: preparing bitmaps.");

        // adding way to prevent wide spread of covid-19
        wayOfPrevent.add("1. Wash your hand FREQUENTLY");
        imageOfPrevent.add("https://media.istockphoto.com/photos/washing-hands-picture-id467706864?k=20&m=467706864&s=612x612&w=0&h=a7dKJxdwY8eSg1Z6g-GzXNjATQChiMr5O_POIA0hfng=");
        detailsOfPrevent.add("Wash your hand for at least 15-20 seconds with soap and water.");

        wayOfPrevent.add("2. Use a hand sanitizer");
        imageOfPrevent.add("data:image/jpeg;base64,");
        detailsOfPrevent.add("Use a hand sanitizer with at least 60% alcohol in it.");

        wayOfPrevent.add("3. Avoid touching your face");
        imageOfPrevent.add("data:image/jpeg;base64,");
        detailsOfPrevent.add("Avoid touching your eyes, nose, and mouth with unsafe and unwashed hands.");

        wayOfPrevent.add("4. Use masks");
        imageOfPrevent.add("data:image/png;base64,");
        detailsOfPrevent.add("Use masks when you are sick and stay at home and isolated from the rest of the family members.");

        wayOfPrevent.add("5. Cover your cough and sneeze");
        imageOfPrevent.add("data:image/jpeg;base64,");
        detailsOfPrevent.add("Cover your cough and sneeze with a tissue and dispose of it properly in the trash.");

        wayOfPrevent.add("6. Avoid close contacts.");
        imageOfPrevent.add("data:image/png;base64,");
        detailsOfPrevent.add("Inside your home: Avoid close contact with people who are sick. If possible, maintain 6 feet between the person who is sick and other household members.");

        wayOfPrevent.add("7. Avoiding social gathering");
        imageOfPrevent.add("data:image/jpeg;base64,");
        detailsOfPrevent.add("Avoiding social gathering, unnecessary foreign traveling, and maintaining distance from the crowd.");

        //calling initRecycleView() function.
        initRecycleViewForPreventive();
    }

